mapper.select:
select order_id, order_no, buyer_name, buyer_account, receiver_name, receiver_phone, receiver_address, order_coupon, goods_title, goods_kind, pay_time, channel
from order_view
where 1 = 1

	and orderId = #{orderId}


	and order_no = #{orderNo}


	and buyer_account = #{buyerAccount}


	and receiver_phone = #{receiverPhone}


	and order_coupon = #{coupon}


	and (order_no like #{search} or buyer_account like #{search} or buyer_name like #{search} or receiver_name like #{search} or receiver_phone like #{search} or order_coupon like #{search} or goods_title like #{search})
