mapper.select:
select
order_id, order_no, order_status, description, buyer_name,
buyer_account, receiver_name, receiver_phone, order_price,
province, city, district, receiver_address, order_channel, order_diversion,
order_coupon, order_time, ship_no, create_time
from guomai_order
WHERE 1 = 1

    AND order_id = #{orderId}


    AND order_no = #{orderNo}


    AND order_status = #{orderStatus}


    AND order_channel = #{orderChannel}


    AND order_diversion = #{orderDiversion}


    AND province = #{province}


    AND city = #{city}


    AND DATE(order_time) >= #{startTime}


    AND DATE(order_time) <= #{endTime}


    AND block_flag = #{blockFlag}

ORDER BY order_time DESC
