mapper.select:
select code_id, batch_no, code_value, code_path, code_delivered
from qrcode
where 1 = 1

	and code_id = #{codeId}


	and code_value = #{value}


	and model_id = #{modelId}


	and goods_id = #{goodsId}


	and batch_no = #{batchNo}

order by create_time desc, batch_no, code_delivered asc, code_value

mapper.update:
update qrcode
set code_delivered = true
where code_id = #{codeId}
