mapper.select:
SELECT chip_id 
FROM device_chip
WHERE status = 1 AND device_id = #{deviceID};

mapper.update:
UPDATE 
deviceName
SET
device_id = #{deviceID}

	,name = #{name}


	,phone = #{phone}


	,province_id = #{provinceID}


	,city_id = #{cityID}


	,address = #{address}


	,version = #{version} + 1

WHERE
device_id = #{deviceID} AND version = #{version}

UPDATE 
deviceShareCode
SET
token = #{token}

	,status = #{status}
	,auth_id = #{authID}

WHERE
token = #{token} AND status = 1

UPDATE user_location
SET status = 0
WHERE user_id = #{userID}

UPDATE qrcode 
SET scan_time = #{scanTime}
WHERE code_value = #{deviceID}
